it("should return once per seconds", () => {
  const rate = new RateLimiter();
  expect(rate.check()).toBe(false);
  expect(rate.check()).toBe(false);
  clock.tick(300);
  expect(rate.check()).toBe(false);
  clock.tick(300);
  expect(rate.check()).toBe(false);
  clock.tick(300);
  expect(rate.check()).toBe(false);
  clock.tick(300);
  expect(rate.check()).toBe(true);
  clock.tick(500);
  expect(rate.check()).toBe(false);
  clock.tick(400);
  expect(rate.check()).toBe(false);
  clock.tick(100);
  expect(rate.check()).toBe(true); // Long times

  clock.tick(3000);
  expect(rate.check()).toBe(true);
  clock.tick(5000);
  expect(rate.check()).toBe(true); // Short times after long time

  clock.tick(100);
  expect(rate.check()).toBe(false);
  clock.tick(100);
  expect(rate.check()).toBe(false);
  clock.tick(100);
  expect(rate.check()).toBe(false);
  clock.tick(100);
  expect(rate.check()).toBe(false);
  clock.tick(800);
  expect(rate.check()).toBe(true);
});

