it("must build structure that maps to text layer", async () => {
  await Promise.all(pages.map(async ([browserName, page]) => {
    await page.waitForSelector(".structTree"); // Check the headings match up.

    const head1 = await page.$eval(".structTree [role='heading'][aria-level='1'] span", el => document.getElementById(el.getAttribute("aria-owns")).textContent);
    expect(head1).withContext(`In ${browserName}`).toEqual("Heading 1");
    const head2 = await page.$eval(".structTree [role='heading'][aria-level='2'] span", el => document.getElementById(el.getAttribute("aria-owns")).textContent);
    expect(head2).withContext(`In ${browserName}`).toEqual("Heading 2"); // Check the order of the content.

    const texts = await page.$$eval(".structTree [aria-owns]", nodes => nodes.map(el => document.getElementById(el.getAttribute("aria-owns")).textContent));
    expect(texts).withContext(`In ${browserName}`).toEqual(["Heading 1", "This paragraph 1.", "Heading 2", "This paragraph 2."]);
  }));
});

it("must show a popup on mouseover", async () => {
