  }));
});

it("must let checkboxes with the same name behave like radio buttons", async () => {
  const selectors = [63, 70, 79].map(n => `[data-annotation-id='${n}R']`);
  await Promise.all(pages.map(async ([browserName, page]) => {
    for (const selector of selectors) {
      await page.click(selector);
      await page.waitForFunction(`document.querySelector("${selector} > :first-child").checked`);

      for (const otherSelector of selectors) {
        const checked = await page.$eval(`${otherSelector} > :first-child`, el => el.checked);
        expect(checked).withContext(`In ${browserName}`).toBe(selector === otherSelector);
      }
    }
  }));
});

it("must update all the fields with the same value", async () => {
