  }));
});

it("must let checkboxes with the same name behave like radio buttons", async () => {
  const text1 = "hello world!";
  const text2 = "!dlrow olleh";
  await Promise.all(pages.map(async ([browserName, page]) => {
    // Text field.
    await page.type(getSelector("64R"), text1); // Checkbox.

    await page.click("[data-annotation-id='65R']"); // Radio.

    await page.click("[data-annotation-id='67R']");

    for (const [pageNumber, textId, checkId, radio1Id, radio2Id] of [[2, "18R", "19R", "21R", "20R"], [5, "23R", "24R", "22R", "25R"]]) {
      await page.evaluate(n => {
        window.document.querySelectorAll(`[data-page-number="${n}"][class="page"]`)[0].scrollIntoView();
      }, pageNumber); // Need to wait to have a displayed text input.

      await page.waitForSelector(getSelector(textId), {
        timeout: 0
      });
      const text = await page.$eval(getSelector(textId), el => el.value);
      expect(text).withContext(`In ${browserName}`).toEqual(text1);
      let checked = await page.$eval(getSelector(checkId), el => el.checked);
      expect(checked).toEqual(true);
      checked = await page.$eval(getSelector(radio1Id), el => el.checked);
      expect(checked).toEqual(false);
      checked = await page.$eval(getSelector(radio2Id), el => el.checked);
      expect(checked).toEqual(false);
    } // Change data on page 5 and check that other pages changed.
    // Text field.


    await page.type(getSelector("23R"), text2); // Checkbox.

    await page.click("[data-annotation-id='24R']"); // Radio.

    await page.click("[data-annotation-id='25R']");

    for (const [pageNumber, textId, checkId, radio1Id, radio2Id] of [[1, "64R", "65R", "67R", "68R"], [2, "18R", "19R", "21R", "20R"]]) {
      await page.evaluate(n => {
        window.document.querySelectorAll(`[data-page-number="${n}"][class="page"]`)[0].scrollIntoView();
      }, pageNumber); // Need to wait to have a displayed text input.

      await page.waitForSelector(getSelector(textId), {
        timeout: 0
      });
      const text = await page.$eval(getSelector(textId), el => el.value);
      expect(text).withContext(`In ${browserName}`).toEqual(text2 + text1);
      let checked = await page.$eval(getSelector(checkId), el => el.checked);
      expect(checked).toEqual(false);
      checked = await page.$eval(getSelector(radio1Id), el => el.checked);
      expect(checked).toEqual(false);
      checked = await page.$eval(getSelector(radio2Id), el => el.checked);
      expect(checked).toEqual(false);
    }
  }));
});

it("must reset all fields", async () => {
  await Promise.all(pages.map(async ([browserName, page]) => {
    const base = "hello world";

    for (let i = 63; i <= 67; i++) {
      await page.type(getSelector(`${i}R`), base);
    }

    const selectors = [69, 71, 75].map(n => `[data-annotation-id='${n}R']`);

    for (const selector of selectors) {
      await page.click(selector);
    }

    await page.select(getSelector("78R"), "b");
    await page.select(getSelector("81R"), "f");
    await page.click("[data-annotation-id='82R']");
    await page.waitForFunction(`${getQuerySelector("63R")}.value === ""`);

    for (let i = 63; i <= 68; i++) {
      const text = await page.$eval(getSelector(`${i}R`), el => el.value);
      expect(text).withContext(`In ${browserName}`).toEqual("");
    }

    const ids = [69, 71, 72, 73, 74, 75, 76, 77];

    for (const id of ids) {
      const checked = await page.$eval(getSelector(`${id}R`), el => el.checked);
      expect(checked).withContext(`In ${browserName}`).toEqual(false);
    }

    let selected = await page.$eval(`${getSelector("78R")} [value="a"]`, el => el.selected);
    expect(selected).withContext(`In ${browserName}`).toEqual(true);
    selected = await page.$eval(`${getSelector("81R")} [value="d"]`, el => el.selected);
    expect(selected).withContext(`In ${browserName}`).toEqual(true);
  }));
});

it("must reset some fields", async () => {
  await Promise.all(pages.map(async ([browserName, page]) => {
    const base = "hello world";

    for (let i = 63; i <= 68; i++) {
      await page.type(getSelector(`${i}R`), base);
    }

    const selectors = [69, 71, 72, 73, 75].map(n => `[data-annotation-id='${n}R']`);

    for (const selector of selectors) {
      await page.click(selector);
    }

    await page.select(getSelector("78R"), "b");
    await page.select(getSelector("81R"), "f");
    await page.click("[data-annotation-id='84R']");
    await page.waitForFunction(`${getQuerySelector("63R")}.value === ""`);

    for (let i = 63; i <= 68; i++) {
      const expected = (i - 3) % 2 === 0 ? "" : base;
      const text = await page.$eval(getSelector(`${i}R`), el => el.value);
      expect(text).withContext(`In ${browserName}`).toEqual(expected);
    }

    let ids = [69, 72, 73, 74, 76, 77];

    for (const id of ids) {
      const checked = await page.$eval(getSelector(`${id}R`), el => el.checked);
      expect(checked).withContext(`In ${browserName + id}`).toEqual(false);
    }

    ids = [71, 75];

    for (const id of ids) {
      const checked = await page.$eval(getSelector(`${id}R`), el => el.checked);
      expect(checked).withContext(`In ${browserName}`).toEqual(true);
    }

    let selected = await page.$eval(`${getSelector("78R")} [value="a"]`, el => el.selected);
    expect(selected).withContext(`In ${browserName}`).toEqual(true);
    selected = await page.$eval(`${getSelector("81R")} [value="f"]`, el => el.selected);
    expect(selected).withContext(`In ${browserName}`).toEqual(true);
  }));
});

it("must highlight text in the right position", async () => {
  await Promise.all(pages.map(async ([browserName, page]) => {
    await page.click("#viewFind");
    await page.waitForSelector("#viewFind", {
      hidden: false
    });
    await page.type("#findInput", "a");
    await page.click("#findHighlightAll");
    await page.waitForSelector(".textLayer .highlight"); // The PDF has the text "AB BA" in a monospace font.
    // Make sure we have the right number of highlighted divs.

    const highlights = await page.$$(".textLayer .highlight");
    expect(highlights.length).withContext(`In ${browserName}`).toEqual(2);
    const glyphWidth = 15.98; // From the PDF.

    const pageDiv = await page.$(".page canvas");
    const pageBox = await pageDiv.boundingBox();
    const firstA = await highlights[0].boundingBox();
    const secondA = await highlights[1].boundingBox(); // Subtract the page offset from the text bounding boxes;

    firstA.x -= pageBox.x;
    firstA.y -= pageBox.y;
    secondA.x -= pageBox.x;
    secondA.y -= pageBox.y; // They should be on the same line.

    expect(firstA.y).withContext(`In ${browserName}`).toEqual(secondA.y);
    const fontSize = 26.66; // From the PDF.
    // The highlighted text has more padding.

    fuzzyMatch(firstA.height, fontSize + 5, browserName);
    fuzzyMatch(secondA.height, fontSize + 5, browserName);
    const expectedFirstAX = 28;
    fuzzyMatch(firstA.x, expectedFirstAX, browserName); // The second 'A' should be 4 glyphs widths from the first.

    fuzzyMatch(secondA.x, expectedFirstAX + glyphWidth * 4, browserName);
  }));
});

