it("must write a string in a FreeText editor", async () => {
  await Promise.all(pages.map(async ([browserName, page]) => {
    await page.click("#editorFreeText");
    const rect = await page.$eval(".annotationEditorLayer", el => {
      // With Chrome something is wrong when serializing a DomRect,
      // hence we extract the values and just return them.
      const {
        x,
        y
      } = el.getBoundingClientRect();
      return {
        x,
        y
      };
    });
    const data = "Hello PDF.js World !!";
    await page.mouse.click(rect.x + 100, rect.y + 100);
    await page.type(`${editorPrefix}0 .internal`, data);
    const editorRect = await page.$eval(`${editorPrefix}0`, el => {
      const {
        x,
        y,
        width,
        height
      } = el.getBoundingClientRect();
      return {
        x,
        y,
        width,
        height
      };
    }); // Commit.

    await page.mouse.click(editorRect.x, editorRect.y + 2 * editorRect.height);
    const content = await page.$eval(`${editorPrefix}0`, el => el.innerText.trimEnd());
    expect(content).withContext(`In ${browserName}`).toEqual(data);
  }));
});

it("must copy/paste", async () => {
  await Promise.all(pages.map(async ([browserName, page]) => {
    const editorRect = await page.$eval(`${editorPrefix}0`, el => {
      const {
        x,
        y,
        width,
        height
      } = el.getBoundingClientRect();
      return {
        x,
        y,
        width,
        height
      };
    }); // Select the editor created previously.

    await page.mouse.click(editorRect.x + editorRect.width / 2, editorRect.y + editorRect.height / 2);
    await page.keyboard.down("Control");
    await page.keyboard.press("c");
    await page.keyboard.up("Control");
    await page.keyboard.down("Control");
    await page.keyboard.press("v");
    await page.keyboard.up("Control");
    const content = await page.$eval(`${editorPrefix}0`, el => el.innerText.trimEnd());
    let pastedContent = await page.$eval(`${editorPrefix}1`, el => el.innerText.trimEnd());
    expect(pastedContent).withContext(`In ${browserName}`).toEqual(content);
    await page.keyboard.down("Control");
    await page.keyboard.press("c");
    await page.keyboard.up("Control");
    await page.keyboard.down("Control");
    await page.keyboard.press("v");
    await page.keyboard.up("Control");
    pastedContent = await page.$eval(`${editorPrefix}2`, el => el.innerText.trimEnd());
    expect(pastedContent).withContext(`In ${browserName}`).toEqual(content);
  }));
});

