    let text = await page.$eval(getSelector("47R"), el => el.value);
    expect(text).withContext(`In ${browserName}`).toEqual("PageOpen 1");

    for (let run = 0; run < 5; run++) {
      for (const ref of ["18R", "19R", "20R", "21R", "47R", "50R"]) {
        await page.evaluate(selector => {
          const element = window.document.querySelector(selector);

          if (element) {
            element.value = "";
          }
        }, getSelector(ref));
      }

      for (const [refOpen, refClose, pageNumOpen, pageNumClose] of [["18R", "50R", 2, 1], ["21R", "19R", 3, 2], ["47R", "20R", 1, 3]]) {
        text = await actAndWaitForInput(page, getSelector(refOpen), async () => {
          await page.evaluate(selector => {
            const element = window.document.querySelector(selector);
            element.scrollIntoView();
          }, getSelector(refOpen));
        }, false);
        expect(text).withContext(`In ${browserName}`).toEqual(`PageOpen ${pageNumOpen}`);
        text = await page.$eval(getSelector(refClose), el => el.value);
        expect(text).withContext(`In ${browserName}`).toEqual(`PageClose ${pageNumClose}`);
      }
    }
  }));
});

