    await page.type(getSelector("27R"), "12.dL", {
      delay: 100
    });
    await page.waitForFunction(`${getQuerySelector("27R")}.value !== "HELLO WOR"`);
    text = await page.$eval(getSelector("27R"), el => el.value);
