it("must check that data-main-rotation is correct", async () => {
  await Promise.all(pages.map(async ([browserName, page]) => {
    await page.waitForFunction("window.PDFViewerApplication.scriptingReady === true");
    let base = 0;

    while (base !== 360) {
      for (const [ref, angle] of [[47, 0], [42, 90], [45, 180], [46, 270]]) {
        const rotation = await page.$eval(`[data-annotation-id='${ref}R']`, el => parseInt(el.getAttribute("data-main-rotation") || 0));
        expect(rotation).withContext(`In ${browserName}`).toEqual((360 + (360 - (base + angle)) % 360) % 360);
      }

      base += 90;
      await page.click(getSelector("48R"));
    }
  }));
});

