it("should only accept comb fields when the flags are valid", async function () {
  const invalidFieldFlags = [AnnotationFieldFlag.MULTILINE, AnnotationFieldFlag.PASSWORD, AnnotationFieldFlag.FILESELECT]; // Start with all invalid flags set and remove them one by one.
  // The field may only use combs when all invalid flags are unset.

  let flags = AnnotationFieldFlag.COMB + AnnotationFieldFlag.MULTILINE + AnnotationFieldFlag.PASSWORD + AnnotationFieldFlag.FILESELECT;
  let promise = Promise.resolve();

  for (let i = 0, ii = invalidFieldFlags.length; i <= ii; i++) {
    promise = promise.then(() => {
      textWidgetDict.set("MaxLen", 20);
      textWidgetDict.set("Ff", flags);
      const textWidgetRef = Ref.get(93, 0);
      const xref = new XRefMock([{
        ref: textWidgetRef,
        data: textWidgetDict
      }]);
      return AnnotationFactory.create(xref, textWidgetRef, pdfManagerMock, idFactoryMock).then(({
        data
      }) => {
        expect(data.annotationType).toEqual(AnnotationType.WIDGET);
        const valid = invalidFieldFlags.length === 0;
        expect(data.comb).toEqual(valid); // Remove the last invalid flag for the next iteration.

        if (!valid) {
          flags -= invalidFieldFlags.pop();
        }
      });
    });
  }

  await promise;
});

