it("should call onSetModified() if value is changed", function () {
  const annotationStorage = new AnnotationStorage();
  let called = false;

  const callback = function () {
    called = true;
  };

  annotationStorage.onSetModified = callback;
  annotationStorage.setValue("asdf", {
    value: "original"
  });
  expect(called).toBe(true); // changing value

  annotationStorage.setValue("asdf", {
    value: "modified"
  });
  expect(called).toBe(true); // not changing value

  called = false;
  annotationStorage.setValue("asdf", {
    value: "modified"
  });
  expect(called).toBe(false);
});

it("should call onResetModified() if set", function () {
  const annotationStorage = new AnnotationStorage();
  let called = false;

  const callback = function () {
    called = true;
  };

  annotationStorage.onResetModified = callback;
  annotationStorage.setValue("asdf", {
    value: "original"
  });
  annotationStorage.resetModified();
  expect(called).toBe(true);
  called = false; // not changing value

  annotationStorage.setValue("asdf", {
    value: "original"
  });
  annotationStorage.resetModified();
  expect(called).toBe(false); // changing value

  annotationStorage.setValue("asdf", {
    value: "modified"
  });
  annotationStorage.resetModified();
  expect(called).toBe(true);
});

