it("creates pdf doc from PDF file protected with user and owner password", async function () {
  const loadingTask = getDocument(buildGetDocumentParams("pr6531_1.pdf"));
  expect(loadingTask instanceof PDFDocumentLoadingTask).toEqual(true);
  const passwordNeededCapability = createPromiseCapability();
  const passwordIncorrectCapability = createPromiseCapability(); // Attach the callback that is used to request a password;
  // similarly to how the default viewer handles passwords.

  loadingTask.onPassword = function (updatePassword, reason) {
    if (reason === PasswordResponses.NEED_PASSWORD && !passwordNeededCapability.settled) {
      passwordNeededCapability.resolve();
      updatePassword("qwerty"); // Provide an incorrect password.

      return;
    }

    if (reason === PasswordResponses.INCORRECT_PASSWORD && !passwordIncorrectCapability.settled) {
      passwordIncorrectCapability.resolve();
      updatePassword("asdfasdf"); // Provide the correct password.

      return;
    } // Shouldn't get here.


    expect(false).toEqual(true);
  };

  const data = await Promise.all([passwordNeededCapability.promise, passwordIncorrectCapability.promise, loadingTask.promise]);
  expect(data[2] instanceof PDFDocumentProxy).toEqual(true);
  await loadingTask.destroy();
});

it("creates pdf doc from PDF file protected with only a user password", async function () {
  const filename = "pr6531_2.pdf";
  const passwordNeededLoadingTask = getDocument(buildGetDocumentParams(filename, {
    password: ""
  }));
  expect(passwordNeededLoadingTask instanceof PDFDocumentLoadingTask).toEqual(true);
  const result1 = passwordNeededLoadingTask.promise.then(function () {
    // Shouldn't get here.
    expect(false).toEqual(true);
    return Promise.reject(new Error("loadingTask should be rejected"));
  }, function (data) {
    expect(data instanceof PasswordException).toEqual(true);
    expect(data.code).toEqual(PasswordResponses.NEED_PASSWORD);
    return passwordNeededLoadingTask.destroy();
  });
  const passwordIncorrectLoadingTask = getDocument(buildGetDocumentParams(filename, {
    password: "qwerty"
  }));
  expect(passwordIncorrectLoadingTask instanceof PDFDocumentLoadingTask).toEqual(true);
  const result2 = passwordIncorrectLoadingTask.promise.then(function () {
    // Shouldn't get here.
    expect(false).toEqual(true);
    return Promise.reject(new Error("loadingTask should be rejected"));
  }, function (data) {
    expect(data instanceof PasswordException).toEqual(true);
    expect(data.code).toEqual(PasswordResponses.INCORRECT_PASSWORD);
    return passwordIncorrectLoadingTask.destroy();
  });
  const passwordAcceptedLoadingTask = getDocument(buildGetDocumentParams(filename, {
    password: "asdfasdf"
  }));
  expect(passwordAcceptedLoadingTask instanceof PDFDocumentLoadingTask).toEqual(true);
  const result3 = passwordAcceptedLoadingTask.promise.then(function (data) {
    expect(data instanceof PDFDocumentProxy).toEqual(true);
    return passwordAcceptedLoadingTask.destroy();
  });
  await Promise.all([result1, result2, result3]);
});

