    exception: UnknownErrorException,
    message: "The reference does not point to a /Page dictionary."
  }, {
    exception: Error,
    message: "Invalid pageIndex request."
  }, {
    exception: Error,
    message: "Invalid pageIndex request."
  }, {
    exception: Error,
    message: "Invalid pageIndex request."
  }];

