it("gets permissions", async function () {
  // Editing not allowed.
  const loadingTask0 = getDocument(buildGetDocumentParams("issue9972-1.pdf"));
  const promise0 = loadingTask0.promise.then(function (pdfDoc) {
    return pdfDoc.getPermissions();
