  const loadingTask = getDocument(buildGetDocumentParams("evaljs.pdf"));
  const pdfDoc = await loadingTask.promise;
  const pdfPage = await pdfDoc.getPage(2);
  pdfDoc.annotationStorage.setValue("30R", {
