it("handles `push` correctly", function () {
  const buffer = new PDFPageViewBuffer(3);
  const viewsMap = createViewsMap(1, 5),
        iterator = viewsMap.values();

  for (let i = 0; i < 3; i++) {
    const view = iterator.next().value;
    buffer.push(view);
  } // Ensure that the correct views are inserted.


  expect([...buffer]).toEqual([viewsMap.get(1), viewsMap.get(2), viewsMap.get(3)]);

  for (let i = 3; i < 5; i++) {
    const view = iterator.next().value;
    buffer.push(view);
  } // Ensure that the correct views are evicted.


  expect([...buffer]).toEqual([viewsMap.get(3), viewsMap.get(4), viewsMap.get(5)]);
});

it("handles `resize` correctly", function () {
  const buffer = new PDFPageViewBuffer(5);
  const viewsMap = createViewsMap(1, 5),
        iterator = viewsMap.values();

  for (let i = 0; i < 5; i++) {
    const view = iterator.next().value;
    buffer.push(view);
  } // Ensure that keeping the size constant won't evict any views.


  buffer.resize(5);
  expect([...buffer]).toEqual([viewsMap.get(1), viewsMap.get(2), viewsMap.get(3), viewsMap.get(4), viewsMap.get(5)]); // Ensure that increasing the size won't evict any views.

  buffer.resize(10);
  expect([...buffer]).toEqual([viewsMap.get(1), viewsMap.get(2), viewsMap.get(3), viewsMap.get(4), viewsMap.get(5)]); // Ensure that decreasing the size will evict the correct views.

  buffer.resize(3);
  expect([...buffer]).toEqual([viewsMap.get(3), viewsMap.get(4), viewsMap.get(5)]);
});

it("handles `resize` correctly, with `idsToKeep` provided", function () {
  const buffer = new PDFPageViewBuffer(5);
  const viewsMap = createViewsMap(1, 5),
        iterator = viewsMap.values();

  for (let i = 0; i < 5; i++) {
    const view = iterator.next().value;
    buffer.push(view);
  } // Ensure that keeping the size constant won't evict any views,
  // while re-ordering them correctly.


  buffer.resize(5, new Set([1, 2]));
  expect([...buffer]).toEqual([viewsMap.get(3), viewsMap.get(4), viewsMap.get(5), viewsMap.get(1), viewsMap.get(2)]); // Ensure that increasing the size won't evict any views,
  // while re-ordering them correctly.

  buffer.resize(10, new Set([3, 4, 5]));
  expect([...buffer]).toEqual([viewsMap.get(1), viewsMap.get(2), viewsMap.get(3), viewsMap.get(4), viewsMap.get(5)]); // Ensure that decreasing the size will evict the correct views,
  // while re-ordering the remaining ones correctly.

  buffer.resize(3, new Set([1, 2, 5]));
  expect([...buffer]).toEqual([viewsMap.get(1), viewsMap.get(2), viewsMap.get(5)]);
});

