it("handles non-dictionary arguments", function () {
  expect(getInheritableProperty({
    dict: null,
    key: "foo"
  })).toEqual(undefined);
  expect(getInheritableProperty({
    dict: undefined,
    key: "foo"
  })).toEqual(undefined);
});

it("handles dictionaries that do not contain the property", function () {
  // Empty dictionary.
  const emptyDict = new Dict();
  expect(getInheritableProperty({
    dict: emptyDict,
    key: "foo"
  })).toEqual(undefined); // Filled dictionary with a different property.

  const filledDict = new Dict();
  filledDict.set("bar", "baz");
  expect(getInheritableProperty({
    dict: filledDict,
    key: "foo"
  })).toEqual(undefined);
});

it("fetches the property if it is not inherited", function () {
  const ref = Ref.get(10, 0);
  const xref = new XRefMock([{
    ref,
    data: "quux"
  }]);
  const dict = new Dict(xref); // Regular values should be fetched.

  dict.set("foo", "bar");
  expect(getInheritableProperty({
    dict,
    key: "foo"
  })).toEqual("bar"); // Array value should be fetched (with references resolved).

  dict.set("baz", ["qux", ref]);
  expect(getInheritableProperty({
    dict,
    key: "baz",
    getArray: true
  })).toEqual(["qux", "quux"]);
});

it("fetches the property if it is inherited and present on one level", function () {
  const ref = Ref.get(10, 0);
  const xref = new XRefMock([{
    ref,
    data: "quux"
  }]);
  const firstDict = new Dict(xref);
  const secondDict = new Dict(xref);
  firstDict.set("Parent", secondDict); // Regular values should be fetched.

  secondDict.set("foo", "bar");
  expect(getInheritableProperty({
    dict: firstDict,
    key: "foo"
  })).toEqual("bar"); // Array value should be fetched (with references resolved).

  secondDict.set("baz", ["qux", ref]);
  expect(getInheritableProperty({
    dict: firstDict,
    key: "baz",
    getArray: true
  })).toEqual(["qux", "quux"]);
});

it("fetches the property if it is inherited and present on multiple levels", function () {
  const ref = Ref.get(10, 0);
  const xref = new XRefMock([{
    ref,
    data: "quux"
  }]);
  const firstDict = new Dict(xref);
  const secondDict = new Dict(xref);
  firstDict.set("Parent", secondDict); // Regular values should be fetched.

  firstDict.set("foo", "bar1");
  secondDict.set("foo", "bar2");
  expect(getInheritableProperty({
    dict: firstDict,
    key: "foo"
  })).toEqual("bar1");
  expect(getInheritableProperty({
    dict: firstDict,
    key: "foo",
    getArray: false,
    stopWhenFound: false
  })).toEqual(["bar1", "bar2"]); // Array value should be fetched (with references resolved).

  firstDict.set("baz", ["qux1", ref]);
  secondDict.set("baz", ["qux2", ref]);
  expect(getInheritableProperty({
    dict: firstDict,
    key: "baz",
    getArray: true,
    stopWhenFound: false
  })).toEqual([["qux1", "quux"], ["qux2", "quux"]]);
});

