it("should not read when errored", async function () {
  let log = "";
  const port = new LoopbackPort();
  const messageHandler2 = new MessageHandler("worker", "main", port);
  messageHandler2.on("fakeHandler", (data, sink) => {
    sink.onPull = function () {
