    };

    log += "0";
    sink.ready.then(() => {
      log += "1";
      sink.enqueue([1, 2, 3, 4], 4);
      return sink.ready;
    }).then(() => {
      log += "e";
      sink.error(new Error("should not read when errored"));
    });
  });
  const messageHandler1 = new MessageHandler("main", "worker", port);
  const readable = messageHandler1.sendWithStream("fakeHandler", {}, {
    highWaterMark: 4,

