it("should send a value, execute an action and get back a new value", async () => {
  function compute(n) {
    let s = 0;

    for (let i = 0; i < n; i++) {
      s += i;
    }

    return s;
  }

  const number = 123;
  const expected = (number - 1) * number / 2;
  const refId = getId();
  const data = {
    objects: {
      field: [{
        id: refId,
        value: "",
        actions: {
          Keystroke: [`${compute.toString()}event.value = compute(parseInt(event.value));`]
        },
        type: "text"
      }]
    },
    calculationOrder: [],
    appInfo: {
      language: "en-US",
      platform: "Linux x86_64"
    }
  };
  sandbox.createSandbox(data);
  await sandbox.dispatchEventInSandbox({
    id: refId,
    value: `${number}`,
    name: "Keystroke",
    willCommit: true
  });
  expect(send_queue.has(refId)).toEqual(true);
  expect(send_queue.get(refId)).toEqual({
    id: refId,
    siblings: null,
    value: expected,
    formattedValue: null
  });
});

