it("should format a number", async () => {
  const refId = getId();
  const data = {
    objects: {
      field: [{
        id: refId,
        value: "",
        actions: {
          test1: [`AFNumber_Format(2, 0, 0, 0, "", false);` + `event.source.value = event.value;`],
          test2: [`AFNumber_Format(1, 3, 0, 0, "$", true);` + `event.source.value = event.value;`],
          test3: [`AFNumber_Format(2, 0, 1, 0, "", false);` + `event.source.value = event.value;`],
          test4: [`AFNumber_Format(2, 0, 2, 0, "", false);` + `event.source.value = event.value;`],
          test5: [`AFNumber_Format(2, 0, 3, 0, "", false);` + `event.source.value = event.value;`]
        },
        type: "text"
      }]
    },
    appInfo: {
      language: "en-US",
      platform: "Linux x86_64"
    },
    calculationOrder: [],
    dispatchEventName: "_dispatchMe"
  };
  sandbox.createSandbox(data);
  await sandbox.dispatchEventInSandbox({
    id: refId,
    value: "123456.789",
    name: "test1"
  });
  expect(send_queue.has(refId)).toEqual(true);
  expect(send_queue.get(refId)).toEqual({
    id: refId,
    value: "123,456.79"
  });
  send_queue.delete(refId);
  await sandbox.dispatchEventInSandbox({
    id: refId,
    value: "223456.789",
    name: "test2"
  });
  expect(send_queue.has(refId)).toEqual(true);
  expect(send_queue.get(refId)).toEqual({
    id: refId,
    value: "$223456,8"
  });
  send_queue.delete(refId);
  await sandbox.dispatchEventInSandbox({
    id: refId,
    value: "-323456.789",
    name: "test3"
  });
  expect(send_queue.has(refId)).toEqual(true);
  expect(send_queue.get(refId)).toEqual({
    id: refId,
    value: "323,456.79",
    textColor: ["RGB", 1, 0, 0]
  });
  send_queue.delete(refId);
  await sandbox.dispatchEventInSandbox({
    id: refId,
    value: "-423456.789",
    name: "test4"
  });
  expect(send_queue.has(refId)).toEqual(true);
  expect(send_queue.get(refId)).toEqual({
    id: refId,
    value: "(423,456.79)"
  });
  send_queue.delete(refId);
  await sandbox.dispatchEventInSandbox({
    id: refId,
    value: "-52345.678",
    name: "test5"
  });
  expect(send_queue.has(refId)).toEqual(true);
  expect(send_queue.get(refId)).toEqual({
    id: refId,
    value: "(52,345.68)",
    textColor: ["RGB", 1, 0, 0]
  });
});

