it("correctly creates a valid URL for allowed protocols", function () {
  // `http` protocol
  expect(createValidAbsoluteUrl("http://www.mozilla.org/foo", null)).toEqual(new URL("http://www.mozilla.org/foo"));
  expect(createValidAbsoluteUrl("/foo", "http://www.mozilla.org")).toEqual(new URL("http://www.mozilla.org/foo")); // `https` protocol

  expect(createValidAbsoluteUrl("https://www.mozilla.org/foo", null)).toEqual(new URL("https://www.mozilla.org/foo"));
  expect(createValidAbsoluteUrl("/foo", "https://www.mozilla.org")).toEqual(new URL("https://www.mozilla.org/foo")); // `ftp` protocol

  expect(createValidAbsoluteUrl("ftp://www.mozilla.org/foo", null)).toEqual(new URL("ftp://www.mozilla.org/foo"));
  expect(createValidAbsoluteUrl("/foo", "ftp://www.mozilla.org")).toEqual(new URL("ftp://www.mozilla.org/foo")); // `mailto` protocol (base URLs have no meaning and should yield `null`)

  expect(createValidAbsoluteUrl("mailto:foo@bar.baz", null)).toEqual(new URL("mailto:foo@bar.baz"));
  expect(createValidAbsoluteUrl("/foo", "mailto:foo@bar.baz")).toEqual(null); // `tel` protocol (base URLs have no meaning and should yield `null`)

  expect(createValidAbsoluteUrl("tel:+0123456789", null)).toEqual(new URL("tel:+0123456789"));
  expect(createValidAbsoluteUrl("/foo", "tel:0123456789")).toEqual(null);
});

it("should resolve with correct data", async function () {
