[1, 3, 5].forEach(num => it(`support using ${num} workers`, async () => {
  const NUM_JOBS = 10;
  const scheduler = createScheduler();
  workers.slice(0, num).forEach(w => {
    scheduler.addWorker(w);
  });
  const rets = await Promise.all(Array(NUM_JOBS).fill(0).map((_, idx) => scheduler.addJob('recognize', `${IMAGE_PATH}/${idx % 2 === 0 ? 'simple' : 'cosmic'}.png`)));
  expect(rets.length).to.be(NUM_JOBS);
}).timeout(60000));
