it('allows decimal values', () => {
  expect(transform(`
    .test {
      margin-top: 0.5px;
    }
  `)).toEqual({
    test: {
      marginTop: 'scalePx2dp(0.5)'
    }
  });
  expect(transform(`
    .test {
      margin-top: 100.5px;
    }
  `)).toEqual({
    test: {
      marginTop: 'scalePx2dp(100.5)'
    }
  });
  expect(transform(`
    .test {
      margin-top: -0.5px;
    }
  `)).toEqual({
    test: {
      marginTop: 'scalePx2dp(-0.5)'
    }
  });
  expect(transform(`
    .test {
      margin-top: -100.5px;
    }
  `)).toEqual({
    test: {
      marginTop: 'scalePx2dp(-100.5)'
    }
  });
  expect(transform(`
    .test {
      margin-top: .5px;
    }
  `)).toEqual({
    test: {
      marginTop: 'scalePx2dp(0.5)'
    }
  });
  expect(transform(`
    .test {
      margin-top: -.5px;
    }
  `)).toEqual({
    test: {
      marginTop: 'scalePx2dp(-0.5)'
    }
  });
});

