it('transforms a block of css', () => {
  expect(transform(`
  .description {
    margin-bottom: 20px;
    font-size: 18px;
    text-align: center;
    color: #656656;
    box-shadow: 10px 20px 30px #fff;
  }

  .container {
    padding: 30px;
    margin-top: 65px;
    align-items: center;
    border: 2px dashed #f00;
  }
`)).toEqual({
    description: {
      fontSize: 'scalePx2dp(18)',
      marginBottom: 'scalePx2dp(20)',
      textAlign: 'center',
      color: '#656656',
