    }
  `)).toEqual({
    test1: {
      paddingBottom: 'scalePx2dp(32)',
      paddingLeft: 'scalePx2dp(32)',
      paddingRight: 'scalePx2dp(32)',
      paddingTop: 'scalePx2dp(32)'
    },
    test2: {
      fontSize: 'scalePx2dp(16)'
    }
  });
});

it('should transform multiple rem values', () => {
  expect(transform(`
    .test1 {
      transform: translate(1rem, 2rem);
    }
    .test2 {
      box-shadow: 1rem 2rem 3rem #fff;
    }
  `)).toEqual({
    test1: {
      transform: [{
        translateY: 'scalePx2dp(32)'
      }, {
        translateX: 'scalePx2dp(16)'
      }]
    },
    test2: {
      shadowColor: '#fff',
      shadowOffset: {
        height: 'scalePx2dp(32)',
        width: 'scalePx2dp(16)'
      },
      shadowRadius: 'scalePx2dp(48)',
      shadowOpacity: 1
    }
  });
});

