      border-radius: 0.5625rem;
    }
  `)).toEqual({
    test1: {
      transform: [{
        translateY: 'scalePx2dp(26)'
      }, {
        translateX: 'scalePx2dp(15)'
      }]
    },
    test2: {
      borderRadius: 'scalePx2dp(9)'
    }
  });
  expect(transform(`
    .test1 {
      transform: translate(.9375rem, 1.625rem);
    }
