it('returns empty object when input is empty', () => {
  expect(transform('')).toEqual({});
});

it('transforms flex', () => {
  expect(transform(`
    .test {
      flex: 1;
    }
  `)).toEqual({
    test: {
      flexBasis: 0,
      flexGrow: 1,
      flexShrink: 1
    }
  });
});

it('transforms numbers', () => {
  expect(transform(`
    .test {
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
    }
  `)).toEqual({
    test: {
      top: 0,
      left: 0,
      right: 0,
      bottom: 0
    }
  });
});

it('ignores unsupported at-rules', () => {
  expect(transform('@charset "utf-8";')).toEqual({});
  expect(transform(`
    @supports (display: grid) {
      div {
        display: grid;
      }
    }
  `)).toEqual({});
});

it('allows pixels in unspecialized transform', () => {
  expect(transform(`
    .test {
      top: 0px;
    }
  `)).toEqual({
    test: {
      top: 'scalePx2dp(0)'
    }
  });
});

it('allows percent in unspecialized transform', () => {
  expect(transform(`
    .test {
      top: 0%;
    }
  `)).toEqual({
    test: {
      top: '0%'
    }
  });
});

it('allows decimal values', () => {
  expect(transform(`
    .test {
      margin-top: 0.5px;
    }
  `)).toEqual({
    test: {
      marginTop: 'scalePx2dp(0.5)'
    }
  });
  expect(transform(`
    .test {
      margin-top: 100.5px;
    }
  `)).toEqual({
    test: {
      marginTop: 'scalePx2dp(100.5)'
    }
  });
  expect(transform(`
    .test {
      margin-top: -0.5px;
    }
  `)).toEqual({
    test: {
      marginTop: 'scalePx2dp(-0.5)'
    }
  });
  expect(transform(`
    .test {
      margin-top: -100.5px;
    }
  `)).toEqual({
    test: {
      marginTop: 'scalePx2dp(-100.5)'
    }
  });
  expect(transform(`
    .test {
      margin-top: .5px;
    }
  `)).toEqual({
    test: {
      marginTop: 'scalePx2dp(0.5)'
    }
  });
  expect(transform(`
    .test {
      margin-top: -.5px;
    }
  `)).toEqual({
    test: {
      marginTop: 'scalePx2dp(-0.5)'
    }
  });
});

it('allows PX or PX values', () => {
  expect(transform(`
    .test {
      top: 1Px;
      margin: 10Px 30px;
    }
  `)).toEqual({
    test: {
      marginBottom: 10,
      marginLeft: 'scalePx2dp(30)',
      marginRight: 'scalePx2dp(30)',
      marginTop: 10,
      top: 1
    }
  });
});

it('allows PX or PX values scalePx2dp', () => {
  expect(transform(`
    .test {
      top: 10Px;
      left:10px;
      margin: 10Px 30px;
    }
  `)).toEqual({
    test: {
      left: 'scalePx2dp(10)',
      marginBottom: 10,
      marginLeft: 'scalePx2dp(30)',
      marginRight: 'scalePx2dp(30)',
      marginTop: 10,
      top: 10
    }
  });
});

