  })

it('does not allow vendor prefixes in values', () => {
  const css = '.test { display: -webkit-flex; }';
  expect.assertions(2);
  return stylelint.lint({
    code: css,
    formatter: 'string',
    config: {
      extends: './index'
    }
  }).then(result => {
    expect(result.errored).toBe(true);
    expect(result.output.includes('value-no-vendor-prefix')).toBe(true);
  });
});

