    expect(searchParams.get('a')).toBe('1');
    expect(searchParams.get('b')).toBe('2');
  }
  {
    const searchParams = new URLSearchParams({
      a: '1',
      b: '2'
    });
    expect(searchParams.keys()).toEqual(['a', 'b']);
    expect(searchParams.get('a')).toBe('1');
    expect(searchParams.get('b')).toBe('2');
  }

  // methods
  {
    const searchParams = new URLSearchParams({
      a: '1'
    });
    expect(searchParams.get('a')).toBe('1');
    searchParams.set('b', '2');
