it('configures a cause when message is a string and the options object is passed', () => {
  const error = new HttpException(customDescription, 400, {
    cause: errorCause
  });
  expect(`${error}`).to.be.eql(`HttpException: ${customDescription}`);
  const {
    cause
  } = error;
  expect(cause).to.be.eql(errorCause);
});

it('configures a cause when using a built-in exception with options', () => {
  const builtInErrorClasses = [BadGatewayException, BadRequestException, ConflictException, ForbiddenException, GatewayTimeoutException, GoneException, HttpVersionNotSupportedException, ImATeapotException, InternalServerErrorException, MethodNotAllowedException, MisdirectedException, NotAcceptableException, NotFoundException, NotImplementedException, PayloadTooLargeException, PreconditionFailedException, RequestTimeoutException, ServiceUnavailableException, UnauthorizedException, UnprocessableEntityException, UnsupportedMediaTypeException];
  builtInErrorClasses.forEach(ExceptionClass => {
    const error = new ExceptionClass(customDescription, {
      cause: errorCause
    });
    const {
      cause
    } = error;
    expect(cause).to.be.eql(errorCause);
  });
});

it('should take only required providers', () => {
  class C {
    static token = 'a';
  }
  const p = [{
    provide: 'a',
    useValue: 'a'
  }, {
    provide: 'b',
    useValue: 'b'
  }, C, {
    provide: 'd',
    useFactory: (c, b) => [c, b],
    inject: [C, {
      token: 'b',
      optional: true
    }, 'x']
  }, {
    provide: 'e',
    useFactory: (d, b) => [d, b],
    inject: ['d', 'b']
  }, {
    provide: 'f',
    useValue: 'f'
  }];
  // should not include 'a' and 'f'
  const expected = p.slice(1, -1);
  const result = getInjectionProviders(p, ['e']);
  expect(result).to.have.length(expected.length);
  expect(result).to.have.members(expected);
  expect(result).not.to.have.members([p[0], p[5]]);
});

