it('should return a ParseFilePipe with no validators', () => {
  const parseFilePipe = parseFilePipeBuilder.build();
  expect(parseFilePipe.getValidators()).to.be.empty;
});

it('should return a ParseFilePipe with MaxSizeValidator and given options', () => {
  const options = {
    maxSize: 1000
  };
  const parseFilePipe = parseFilePipeBuilder.addMaxSizeValidator(options).build();
  expect(parseFilePipe.getValidators()).to.deep.include(new MaxFileSizeValidator(options));
});

it('should return a ParseFilePipe with FileTypeValidator and given options', () => {
  const options = {
    fileType: 'image/jpeg'
  };
  const parseFilePipe = parseFilePipeBuilder.addFileTypeValidator(options).build();
  expect(parseFilePipe.getValidators()).to.deep.include(new FileTypeValidator(options));
});

it('should return a ParseFilePipe with TestFileValidator and given options', () => {
  class TestFileValidator extends FileValidator {
    buildErrorMessage(file) {
      return 'TestFileValidator failed';
    }
    isValid(file) {
      return true;
    }
  }
  const options = {
    name: 'test'
  };
  const parseFilePipe = parseFilePipeBuilder.addValidator(new TestFileValidator(options)).build();
  expect(parseFilePipe.getValidators()).to.deep.include(new TestFileValidator(options));
});

it('should not reuse validators', () => {
  const maxSizeValidatorOptions = {
    maxSize: 1000
  };
  const pipeWithMaxSizeValidator = parseFilePipeBuilder.addMaxSizeValidator(maxSizeValidatorOptions).build();
  const fileTypeValidatorOptions = {
    fileType: 'image/jpeg'
  };
  const pipeWithFileTypeValidator = parseFilePipeBuilder.addFileTypeValidator(fileTypeValidatorOptions).build();
  expect(pipeWithFileTypeValidator.getValidators()).not.to.deep.equal(pipeWithMaxSizeValidator.getValidators());
});

