it('should return valid, concatenated paths (various combinations)', () => {
  expect(
    routePathFactory.create({
      ctrlPath: 'ctrlPath/',
      methodPath: '',
    }),
  ).to.deep.equal(['/ctrlPath']);

  expect(
    routePathFactory.create({
      ctrlPath: '/ctrlPath',
      methodPath: '',
    }),
  ).to.deep.equal(['/ctrlPath']);

  expect(
    routePathFactory.create({
      ctrlPath: '/ctrlPath/',
      methodPath: '/methodPath',
    }),
  ).to.deep.equal(['/ctrlPath/methodPath']);

  expect(
    routePathFactory.create({
      ctrlPath: 'ctrlPath/',
      methodPath: 'methodPath/',
    }),
  ).to.deep.equal(['/ctrlPath/methodPath']);

  expect(
    routePathFactory.create({
      ctrlPath: 'ctrlPath/',
      methodPath: 'methodPath',
      modulePath: 'modulePath',
    }),
  ).to.deep.equal(['/modulePath/ctrlPath/methodPath']);

  expect(
    routePathFactory.create({
      ctrlPath: 'ctrlPath/',
      methodPath: 'methodPath',
      modulePath: '/modulePath',
    }),
  ).to.deep.equal(['/modulePath/ctrlPath/methodPath']);

  expect(
    routePathFactory.create({
      ctrlPath: '/ctrlPath/',
      methodPath: '/methodPath/',
      modulePath: '/modulePath/',
    }),
  ).to.deep.equal(['/modulePath/ctrlPath/methodPath']);

  expect(
    routePathFactory.create({
      ctrlPath: '/ctrlPath/',
      methodPath: '/methodPath/',
      modulePath: '/modulePath/',
      globalPrefix: 'api',
    }),
  ).to.deep.equal(['/api/modulePath/ctrlPath/methodPath']);

  expect(
    routePathFactory.create({
      ctrlPath: '/ctrlPath/',
      methodPath: '/methodPath/',
      modulePath: '/modulePath/',
      globalPrefix: '/api',
    }),
  ).to.deep.equal(['/api/modulePath/ctrlPath/methodPath']);

  expect(
    routePathFactory.create({
      ctrlPath: '/ctrlPath/',
      methodPath: '/methodPath/',
      modulePath: '/modulePath/',
      globalPrefix: '/api',
      versioningOptions: {
        type: VersioningType.HEADER,
        header: 'x',
      },
      methodVersion: '1.0.0',
      controllerVersion: '1.1.1',
    }),
  ).to.deep.equal(['/api/modulePath/ctrlPath/methodPath']);

  expect(
    routePathFactory.create({
      ctrlPath: '/ctrlPath/',
      methodPath: '/methodPath/',
      modulePath: '/modulePath/',
      globalPrefix: '/api/',
      versioningOptions: {
        type: VersioningType.URI,
      },
      methodVersion: '1.0.0',
      controllerVersion: '1.1.1',
    }),
  ).to.deep.equal(['/api/v1.0.0/modulePath/ctrlPath/methodPath']);

  expect(
    routePathFactory.create({
      ctrlPath: '/ctrlPath/',
      methodPath: '/methodPath/',
      modulePath: '/modulePath/',
      versioningOptions: {
        type: VersioningType.URI,
      },
      methodVersion: '1.0.0',
      controllerVersion: '1.1.1',
    }),
  ).to.deep.equal(['/v1.0.0/modulePath/ctrlPath/methodPath']);

  expect(
    routePathFactory.create({
      ctrlPath: '/ctrlPath/',
      methodPath: '/methodPath/',
      globalPrefix: '/api',
      versioningOptions: {
        type: VersioningType.URI,
      },
      methodVersion: '1.0.0',
      controllerVersion: '1.1.1',
    }),
  ).to.deep.equal(['/api/v1.0.0/ctrlPath/methodPath']);

  expect(
    routePathFactory.create({
      ctrlPath: '/ctrlPath/',
      methodPath: '/methodPath/',
      globalPrefix: '/api',
      versioningOptions: {
        type: VersioningType.URI,
      },
      controllerVersion: '1.1.1',
    }),
  ).to.deep.equal(['/api/v1.1.1/ctrlPath/methodPath']);

  expect(
    routePathFactory.create({
      ctrlPath: '/ctrlPath/',
      methodPath: '/methodPath/',
      globalPrefix: '/api',
      versioningOptions: {
        type: VersioningType.URI,
      },
      controllerVersion: ['1.1.1', '1.2.3'],
    }),
  ).to.deep.equal([
    '/api/v1.1.1/ctrlPath/methodPath',
    '/api/v1.2.3/ctrlPath/methodPath',
  ]);

  expect(
    routePathFactory.create({
      ctrlPath: '',
      methodPath: '',
      globalPrefix: '/api',
      versioningOptions: {
        type: VersioningType.URI,
      },
      controllerVersion: ['1.1.1', '1.2.3'],
    }),
  ).to.deep.equal(['/api/v1.1.1', '/api/v1.2.3']);

  expect(
    routePathFactory.create({
      ctrlPath: '',
      methodPath: '',
      globalPrefix: '',
      controllerVersion: VERSION_NEUTRAL,
      versioningOptions: {
        type: VersioningType.URI,
        defaultVersion: VERSION_NEUTRAL,
      },
    }),
  ).to.deep.equal(['/']);

  expect(
    routePathFactory.create({
      ctrlPath: '',
      methodPath: '',
      globalPrefix: '',
      controllerVersion: ['1', VERSION_NEUTRAL],
      versioningOptions: {
        type: VersioningType.URI,
        defaultVersion: ['1', VERSION_NEUTRAL],
      },
    }),
  ).to.deep.equal(['/v1', '/']);

  expect(
    routePathFactory.create({
      ctrlPath: '',
      methodPath: '',
      globalPrefix: '',
    }),
  ).to.deep.equal(['/']);

  sinon.stub(routePathFactory, 'isExcludedFromGlobalPrefix').returns(true);
  expect(
    routePathFactory.create({
      ctrlPath: '/ctrlPath/',
      methodPath: '/',
      modulePath: '/',
      globalPrefix: '/api',
    }),
  ).to.deep.equal(['/ctrlPath']);
  sinon.restore();
});

