it('should timeout waiting for link return', function (done) {
  this.timeout(1000);
  const flow = [{
    id: "tab-flow-1",
    type: "tab",
    label: "Flow 1"
  }, {
    id: "link-in-1",
    z: "tab-flow-1",
    type: "link in",
    name: "double payload",
    wires: [["func"]]
  }, {
    id: "func",
    z: "tab-flow-1",
    type: "helper",
    wires: [["link-out-1"]]
  }, {
    id: "link-out-1",
    z: "tab-flow-1",
    type: "link out",
    mode: ""
  },
  //not return mode, cause link-call timeout
  {
    id: "link-call",
    z: "tab-flow-1",
    type: "link call",
    linkType: "static",
    "timeout": "0.5",
    links: ["link-in-1"],
    wires: [["n4"]]
  }, {
    id: "catch-all",
    z: "tab-flow-1",
    type: "catch",
    scope: ["link-call"],
    uncaught: true,
    wires: [["n4"]]
  }, {
    id: "n4",
    z: "tab-flow-1",
    type: "helper"
  }];
  helper.load(linkNode, flow, function () {
    const funcNode = helper.getNode("func");
    const linkCallNode = helper.getNode("link-call");
    const helperNode = helper.getNode("n4");
    funcNode.on("input", function (msg, send, done) {
      msg.payload += msg.payload;
      send(msg);
      done();
    });
    helperNode.on("input", function (msg) {
      try {
        msg.should.have.property("target", "double payload");
        msg.should.have.property("error");
        msg.error.should.have.property("message", "timeout");
        msg.error.should.have.property("source");
        done();
      } catch (err) {
        done(err);
      }
    });
    linkCallNode.receive({
      payload: "hello",
      target: "double payload"
    });
  });
});

