it('should check input against a previous value', function (done) {
  var flow = [{
    id: "switchNode1",
    type: "switch",
    name: "switchNode",
    property: "payload",
    rules: [{
      "t": "gt",
      "v": "",
      "vt": "prev"
    }],
    checkall: true,
    outputs: 1,
    wires: [["helperNode1"]]
  }, {
    id: "helperNode1",
    type: "helper",
    wires: []
  }];
  helper.load(switchNode, flow, function () {
    var switchNode1 = helper.getNode("switchNode1");
    var helperNode1 = helper.getNode("helperNode1");
    var c = 0;
    helperNode1.on("input", function (msg) {
      if (msg.payload) {
        try {
          if (c === 0) {
            msg.payload.should.equal(1);
          }
          if (c === 1) {
            msg.payload.should.equal(2);
            done();
          }
          c += 1;
        } catch (err) {
          done(err);
        }
      } else {
        done();
      }
    });
    switchNode1.receive({
      payload: 1
    });
    switchNode1.receive({
      payload: 0
    });
    switchNode1.receive({
      payload: -2
    });
    switchNode1.receive({
      payload: 2
    });
  });
});

