it('handles more than one switch statement', function (done) {
  var flow = [{
    id: "switchNode1",
    type: "switch",
    name: "switchNode",
    property: "payload",
    rules: [{
      "t": "eq",
      "v": "Hello"
    }, {
      "t": "cont",
      "v": "ello"
    }, {
      "t": "else"
    }],
    checkall: true,
    outputs: 3,
    wires: [["helperNode1"], ["helperNode2"], ["helperNode3"]]
  }, {
    id: "helperNode1",
    type: "helper",
    wires: []
  }, {
    id: "helperNode2",
    type: "helper",
    wires: []
  }, {
    id: "helperNode3",
    type: "helper",
    wires: []
  }];
  helper.load(switchNode, flow, function () {
    var switchNode1 = helper.getNode("switchNode1");
    var helperNode1 = helper.getNode("helperNode1");
    var helperNode2 = helper.getNode("helperNode2");
    var helperNode3 = helper.getNode("helperNode3");
    var nodeHitCount = 0;
    helperNode1.on("input", function (msg) {
      try {
        msg.payload.should.equal("Hello");
        nodeHitCount++;
      } catch (err) {
        done(err);
      }
    });
    helperNode2.on("input", function (msg) {
      try {
        msg.payload.should.equal("Hello");
        nodeHitCount++;
        if (nodeHitCount == 2) {
          done();
        } else {
          try {
            should.fail(null, null, "Both statements should be triggered!");
          } catch (err) {
            done(err);
          }
        }
      } catch (err) {
        done(err);
      }
    });
    helperNode3.on("input", function (msg) {
      try {
        should.fail(null, null, "The otherwise/else statement should not be triggered here!");
      } catch (err) {
        done(err);
      }
    });
    switchNode1.receive({
      payload: "Hello"
    });
  });
});

