it('handles delay queue', function (done) {
  this.timeout(2000);
  var flow = [{
    id: "delayNode1",
    type: "delay",
    "name": "delayNode",
    "nbRateUnits": "1",
    "pauseType": "queue",
    "timeout": 1,
    "timeoutUnits": "seconds",
    "rate": 4,
    "rateUnits": "second",
    "randomFirst": "1",
    "randomLast": "5",
    "randomUnits": "seconds",
    "drop": false,
    "wires": [["helperNode1"]]
  }, {
    id: "helperNode1",
    type: "helper",
    wires: []
  }];
  helper.load(delayNode, flow, function () {
    var delayNode1 = helper.getNode("delayNode1");
    var helperNode1 = helper.getNode("helperNode1");
    var t = Date.now();
    helperNode1.on("input", function (msg) {
      msg.should.have.a.property('payload');
      msg.should.have.a.property('topic');
      try {
        if (msg.topic === "_none_") {
          msg.payload.should.equal(2);
          (Date.now() - t).should.be.approximately(500, 200);
        } else if (msg.topic === "A") {
          msg.payload.should.equal(4);
          (Date.now() - t).should.be.approximately(750, 200);
        } else {
          msg.topic.should.equal("B");
          msg.payload.should.equal(1);
          (Date.now() - t).should.be.approximately(1000, 200);
          done();
        }
      } catch (e) {
        done(e);
      }
    });
    setTimeout(function () {
      // send test messages
      delayNode1.receive({
        payload: 1
      }); // send something with blank topic
      delayNode1.receive({
        payload: 1,
        topic: "A"
      }); // and something with a fixed topic
      delayNode1.receive({
        payload: 1,
        topic: "B"
      }); // and something else with a fixed topic (3rd tick)
      delayNode1.receive({
        payload: 2,
        topic: "A"
      }); // these should replace them in queue
      delayNode1.receive({
        payload: 3,
        topic: "A"
      }); //  ditto
      delayNode1.receive({
        payload: 2
      }); // so only this should get out on first tick
      delayNode1.receive({
        payload: 4,
        topic: "A"
      }); // and this one on second tick
    }, 275); // wait one tick beofre starting.. (to test no messages in queue path.)
  });
});

