it('should be able to return things from persistable flow and global context variables', function (done) {
  var flow = [{
    "id": "n1",
    "type": "trigger",
    "name": "triggerNode",
    "op1": "#:(memory1)::foo",
    "op1type": "flow",
    "op2": "#:(memory1)::bar",
    "op2type": "global",
    "duration": "20",
    "wires": [["n2"]],
    "z": "flow"
  }, {
    "id": "n2",
    "type": "helper"
  }];
  helper.load(triggerNode, flow, function () {
    initContext(function () {
      var n1 = helper.getNode("n1");
      var n2 = helper.getNode("n2");
      var c = 0;
      n2.on("input", function (msg) {
        try {
          if (c === 0) {
            msg.should.have.a.property("payload", "foo");
            c += 1;
          } else {
            msg.should.have.a.property("payload", "bar");
            done();
          }
        } catch (err) {
          done(err);
        }
      });
      var context = n1.context();
      var flow = context.flow;
      var global = context.global;
      flow.set("foo", "foo", "memory1", function (err) {
        global.set("bar", "bar", "memory1", function (err) {
          n1.emit("input", {
            payload: null
          });
        });
      });
    });
  });
});

it('should be able to return things from multiple persistable global context variables', function (done) {
  var flow = [{
    "id": "n1",
    "z": "flow",
    "type": "trigger",
    "duration": "20",
    "wires": [["n2"]],
    "op1": "#:(memory1)::val",
    "op1type": "global",
    "op2": "#:(memory2)::val",
    "op2type": "global"
  }, {
    "id": "n2",
    "type": "helper"
  }];
  helper.load(triggerNode, flow, function () {
    initContext(function () {
      var n1 = helper.getNode("n1");
      var n2 = helper.getNode("n2");
      var count = 0;
      n2.on("input", function (msg) {
        try {
          if (count === 0) {
            msg.should.have.a.property("payload", "foo");
          } else {
            msg.should.have.a.property("payload", "bar");
          }
          count++;
          if (count === 1) {
            done();
          }
        } catch (err) {
