it('should be able to set infinite timeout, and clear timeout', function (done) {
  var flow = [{
    "id": "n1",
    "type": "trigger",
    "name": "triggerNode",
    duration: "0",
    extend: false,
    wires: [["n2"]]
  }, {
    id: "n2",
    type: "helper"
  }];
  helper.load(triggerNode, flow, function () {
    var n1 = helper.getNode("n1");
    var n2 = helper.getNode("n2");
    var c = 0;
    n2.on("input", function (msg) {
      try {
        c += 1;
        msg.should.have.a.property("payload", "1");
      } catch (err) {
        done(err);
      }
    });
    setTimeout(function () {
      if (c === 2) {
        done();
      } else {
        done(new Error("Too many messages received"));
      }
    }, 20);
    n1.emit("input", {
      payload: null
    }); // trigger
    n1.emit("input", {
      payload: null
    }); // blocked
    n1.emit("input", {
      payload: null
    }); // blocked
    n1.emit("input", {
      reset: true
    }); // clear the blockage
    n1.emit("input", {
      payload: null
    }); // trigger
  });
});

