        socks[0].on("message", function (msg, flags) {
          try {
            msg.should.equal("hello");
            resolve();
          } catch (err) {
            reject(err);
          }
        });
      }), new Promise((resolve, reject) => {
        socks[1].on("message", function (msg, flags) {
          try {
            msg.should.equal("hello");
            resolve();
          } catch (err) {
            reject(err);
          }
        });
      })];
      helper.getNode("n3").send({
        payload: "hello"
      });
      return Promise.all(promises).then(() => {
        done();
      });
    }).catch(function (err) {
      done(err);
    });
  });
});

