    id: "server",
    type: "websocket-listener",
    path: "/ws",
    wholemsg: "true"
  }, {
    id: "client",
    type: "websocket-client",
    path: getWsUrl("/ws"),
    wholemsg: "true"
  }, {
    id: "n1",
    type: "websocket in",
    client: "client",
    wires: [["n2", "output"]]
  }, {
    id: "n2",
    type: "websocket out",
    server: "server"
  }, {
    id: "n3",
    type: "helper",
    wires: [["n2"]]
  }, {
    id: "output",
    type: "helper"
  }];
  helper.load(websocketNode, flow, function () {
    getSocket('client').on('open', function () {
      helper.getNode("n3").send({
        payload: "ping"
      });
    });
    var acc = 0;
    helper.getNode("output").on("input", function (msg) {
      acc = acc + 1;
    });
    setTimeout(function () {
      acc.should.equal(1);
      helper.clearFlows();
      done();
    }, 250);
  });
});

it('should report error if no server config', function (done) {
