  }, {
    id: "n2",
    type: "helper"
  }];
  helper.load(csvNode, flow, function () {
    var n1 = helper.getNode("n1");
    var n2 = helper.getNode("n2");
    n2.on("input", function (msg) {
      msg.should.have.property('payload', {
