it('should append to a file and add newline', function (done) {
  var flow = [{
    id: "fileNode1",
    type: "file",
    name: "fileNode",
    "filename": fileToTest,
    "appendNewline": true,
    "overwriteFile": false,
    wires: [["helperNode1"]]
  }, {
    id: "helperNode1",
    type: "helper"
  }];
  try {
    fs.unlinkSync(fileToTest);
  } catch (err) {}
  helper.load(fileNode, flow, function () {
    var n1 = helper.getNode("fileNode1");
    var n2 = helper.getNode("helperNode1");
    var count = 0;
    var data = ["test2", true, 999, [2]];
    n2.on("input", function (msg) {
      try {
        msg.should.have.property("payload");
        data.should.containDeep([msg.payload]);
        if (count === 3) {
          var f = fs.readFileSync(fileToTest).toString();
          if (os.type() !== "Windows_NT") {
            f.should.have.length(19);
            f.should.equal("test2\ntrue\n999\n[2]\n");
          } else {
            f.should.have.length(23);
            f.should.equal("test2\r\ntrue\r\n999\r\n[2]\r\n");
          }
          done();
        }
        count++;
      } catch (e) {
        done(e);
      }
    });
    n1.receive({
      payload: "test2"
    }); // string
    setTimeout(function () {
      n1.receive({
        payload: true
      }); // boolean
    }, 30);
    setTimeout(function () {
      n1.receive({
        payload: 999
      }); // number
    }, 60);
    setTimeout(function () {
      n1.receive({
        payload: [2]
      }); // object (array)
    }, 90);
  });
});

it('should append to a file after it has been deleted ', function (done) {
  var flow = [{
    id: "fileNode1",
    type: "file",
    name: "fileNode",
    "filename": fileToTest,
    "appendNewline": false,
    "overwriteFile": false,
    wires: [["helperNode1"]]
  }, {
    id: "helperNode1",
    type: "helper"
  }];
  try {
    fs.unlinkSync(fileToTest);
  } catch (err) {}
  helper.load(fileNode, flow, function () {
    var n1 = helper.getNode("fileNode1");
    var n2 = helper.getNode("helperNode1");
    var data = ["one", "two", "three", "four"];
    var count = 0;
    n2.on("input", function (msg) {
      try {
        msg.should.have.property("payload");
        data.should.containDeep([msg.payload]);
        try {
          if (count === 1) {
            // Check they got appended as expected
            var f = fs.readFileSync(fileToTest).toString();
            f.should.equal("onetwo");

            // Delete the file
            fs.unlinkSync(fileToTest);
            setTimeout(function () {
              // Send two more messages to the file
              n1.receive({
                payload: "three"
              });
              n1.receive({
                payload: "four"
              });
            }, wait);
          }
          if (count === 3) {
            var f = fs.readFileSync(fileToTest).toString();
            f.should.equal("threefour");
            fs.unlinkSync(fileToTest);
            done();
          }
        } catch (err) {
          done(err);
        }
        count++;
      } catch (e) {
        done(e);
      }
    });

    // Send two messages to the file
    n1.receive({
      payload: "one"
    });
    n1.receive({
      payload: "two"
    });
  });
});

it('should append to a file after it has been recreated ', function (done) {
  var flow = [{
    id: "fileNode1",
    type: "file",
    name: "fileNode",
    "filename": fileToTest,
    "appendNewline": false,
    "overwriteFile": false,
    wires: [["helperNode1"]]
  }, {
    id: "helperNode1",
    type: "helper"
  }];
  try {
    fs.unlinkSync(fileToTest);
  } catch (err) {}
  helper.load(fileNode, flow, function () {
    var n1 = helper.getNode("fileNode1");
    var n2 = helper.getNode("helperNode1");
    var data = ["one", "two", "three", "four"];
    var count = 0;
    n2.on("input", function (msg) {
      try {
        msg.should.have.property("payload");
        data.should.containDeep([msg.payload]);
        if (count == 1) {
          // Check they got appended as expected
          var f = fs.readFileSync(fileToTest).toString();
          f.should.equal("onetwo");
          if (os.type() === "Windows_NT") {
            var dummyFile = path.join(resourcesDir, "50-file-test-dummy.txt");
            fs.rename(fileToTest, dummyFile, function () {
              recreateTest(n1, dummyFile);
            });
          } else {
            recreateTest(n1, fileToTest);
          }
        }
        if (count == 3) {
          // Check the file was updated
          try {
            var f = fs.readFileSync(fileToTest).toString();
            f.should.equal("threefour");
            fs.unlinkSync(fileToTest);
            done();
          } catch (err) {
            done(err);
          }
        }
      } catch (err) {
        done(err);
      }
      count++;
    });

    // Send two messages to the file
    n1.receive({
      payload: "one"
    });
    n1.receive({
      payload: "two"
    });
  });
  function recreateTest(n1, fileToDelete) {
    // Delete the file
    fs.unlinkSync(fileToDelete);

    // Recreate it
    fs.writeFileSync(fileToTest, "");

    // Send two more messages to the file
    n1.receive({
      payload: "three"
    });
    n1.receive({
      payload: "four"
    });
  }
});

