    diagnostics: {
      get: async function (opts) {
        return new Promise(function (resolve, reject) {
          opts = opts || {};
          try {
            resolve({
              opts: opts
            });
          } catch (error) {
            error.status = 500;
            reject(error);
          }
        });
      }
    }
  };
  diagnostics.init(settings, runtimeAPI);
  request(app).get("/diagnostics").expect(403).end(function (err, res) {
