it("expires websocket sessions", function (done) {
  var ws = new WebSocket(url);
  var received = 0;
  ws.on('open', function () {
    ws.send('{"auth":"1234"}');
  });
  ws.on('message', function (msg) {
    received++;
    if (received == 3) {
      msg.should.equal('{"auth":"fail"}');
    } else if (received == 1) {
      msg.should.equal('{"auth":"ok"}');
      ws.send('{"subscribe":"foo"}');
      connections[0].send('foo', 'correct');
    } else {
      msg.should.equal('[{"topic":"foo","data":"correct"}]');
      setTimeout(function () {
        onSessionExpiryCallback({
          accessToken: "1234"
        });
      }, 50);
    }
  });
  ws.on('close', function () {
    try {
      received.should.equal(3);
      done();
    } catch (err) {
      done(err);
    }
  });
});

it('allows anonymous connections that do not authenticate', function (done) {
  var ws = new WebSocket(url);
  var count = 0;
  var interval;
  ws.on('open', function () {
    ws.send('{"subscribe":"foo"}');
    setTimeout(function () {
      connections[0].send('foo', 'correct');
    }, 200);
  });
  ws.on('message', function (msg) {
    msg.should.equal('[{"topic":"foo","data":"correct"}]');
    count++;
    ws.close();
  });
  ws.on('close', function () {
    try {
      count.should.equal(1);
      done();
    } catch (err) {
      done(err);
    }
  });
});

