    settings: {
      a: {
        exportable: true
      },
      b: {
        exportable: false
      },
      d: {
        exportable: true,
        value: 456
      }
    }
  });
  var exportedSet = {};
  plugins.exportPluginSettings(exportedSet);
  exportedSet.should.have.property("a-plugin");
