it("instantiates a node with environment variable property values", function (done) {
  after(function () {
    delete process.env.NODE_RED_TEST_VALUE;
  });
  process.env.NODE_RED_TEST_VALUE = "a-value";
  var config = flowUtils.parseConfig([{
    id: "t1",
    type: "tab"
  }, {
    id: "1",
    x: 10,
    y: 10,
    z: "t1",
    type: "test",
    foo: "$(NODE_RED_TEST_VALUE)",
    wires: []
  }, {
    id: "2",
    x: 10,
    y: 10,
    z: "t1",
    type: "test",
    foo: {
      a: "$(NODE_RED_TEST_VALUE)"
    },
    wires: []
  }, {
    id: "3",
    x: 10,
    y: 10,
    z: "t1",
    type: "test",
    foo: " $(NODE_RED_TEST_VALUE)",
    wires: []
  }, {
    id: "4",
    x: 10,
    y: 10,
    z: "t1",
    type: "test",
    foo: "$(NODE_RED_TEST_VALUE) ",
    wires: []
  }, {
    id: "5",
    x: 10,
    y: 10,
    z: "t1",
    type: "test",
    foo: "$(NODE_RED_TEST_VALUE_NONE)",
    wires: []
  }, {
    id: "6",
    x: 10,
    y: 10,
    z: "t1",
    type: "test",
    foo: ["$(NODE_RED_TEST_VALUE)"],
    wires: []
  }]);
  var flow = Flow.create({
    getSetting: v => process.env[v]
  }, config, config.flows["t1"]);
  flow.start();
  var activeNodes = flow.getActiveNodes();
  activeNodes["1"].foo.should.equal("a-value");
  activeNodes["2"].foo.a.should.equal("a-value");
