  activeNodes["4"].foo.should.equal("$(NODE_RED_TEST_VALUE) ");
  activeNodes["5"].foo.should.equal("$(NODE_RED_TEST_VALUE_NONE)");
  activeNodes["6"].foo[0].should.equal("a-value");
  flow.stop().then(function () {
    done();
  });
});

