    x: 10,
    y: 10,
    z: "t1",
    type: "subflow:sf1",
    wires: ["3"]
  }, {
    id: "3",
    x: 10,
    y: 10,
    z: "t1",
    type: "test",
    foo: "a",
    wires: []
  }, {
    id: "4",
    x: 10,
    y: 10,
    z: "t1",
    type: "test",
    foo: "a",
    wires: []
  }, {
    id: "sf1",
