it('handles ENV substitutions in an object - $()', function () {
  var foo = {
    a: "$(foo1)",
    b: "$(foo2)",
    c: {
      d: "$(foo3)"
    }
  };
  for (var p in foo) {
    if (foo.hasOwnProperty(p)) {
      flowUtil.mapEnvVarProperties(foo, p, {
        getSetting: p => process.env[p]
      });
    }
  }
  foo.should.eql({
    a: 'bar1',
    b: 'bar2',
    c: {
      d: 'bar3'
    }
  });
});

