});

it('ignores x/y changes', function () {
  flowUtil.diffNodes({
    x: 10,
    y: 10
  }, {
    x: 20,
    y: 10
  }).should.be.false();
  flowUtil.diffNodes({
    x: 10,
    y: 10
  }, {
    x: 10,
    y: 20
  }).should.be.false();
});

